import { useNavigate } from 'react-router-dom';
import React, { useEffect } from 'react';
import CreateTask from '../modals/CreateTask';
import { useState } from 'react';
import Card from './Card';
import Search from './Search';
import "../components/signup.css";
import "../components/todolist.css"

const ToDoList = ({ tasks, loggedIn, deleteTask, handleNewPost }) => {
  const [modal, setModal] = useState(false);
  const [filteredTasks, setFilteredTasks] = useState(tasks);

  const toggle = () => {
    setModal(!modal);
  };

  async function searchFunction(searchTerms) {
    if (searchTerms.trim() === "") {
      setFilteredTasks(tasks);
    } else {
      const filtered = tasks.filter((item) =>
        item.description.toLowerCase().includes(searchTerms.toLowerCase())
      );
      setFilteredTasks(filtered);
    }
  }

  return (
    <>
      <div className='header text-center'>
        <h1>TASK MANAGER</h1>
        <Search searchFunction={searchFunction} />
        <button className='btn btn-primary mt-2' onClick={() => setModal(true)}>
          Create Task
        </button>
        <div className='logout-form'>
          <a id='logout' href='/'>
            LogOut
          </a>
        </div>
      </div>

      <div className='task-container'>
        {filteredTasks.map((obj) => (
          <Card taskObj={obj} handleNewPost={handleNewPost} loggedIn={loggedIn} deleteTask={deleteTask} />
        ))}
      </div>

      <CreateTask toggle={toggle} handleNewPost={handleNewPost} modal={modal} loggedIn={loggedIn} />
    </>
  );
};

export default ToDoList;
