import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';

import { useEffect, useState } from 'react';
import { Routes, Route } from 'react-router-dom';
import ToDoList from './components/ToDoList';
import Signup from './components/Signup';
import Login from './components/Login';



function App() {

  const [loggedIn, setLoggedIn] = useState({})
  const [tasks, setTasks] = useState([])
  const [post, setPosts] = useState(true)

  const addNewUser = (newUser) => setLoggedIn(newUser);


  useEffect(() => {
    fetch(`http://localhost:9293/users/${loggedIn.id}/tasks`)
    .then(r => r.json())
    .then( (tasksarr) => {
      console.log(tasksarr)
      setTasks(tasksarr)
    })
    }, [loggedIn, post])

    const deleteTask = (id) => {
        const newTasks = tasks.filter(task => task.id !== id)
        setTasks(newTasks)
    }

    function handleNewPost() {
      setPosts((post) => !post)
    }

    const [search, setSearch] = React.useState('')
  const filterTasks = tasks.filter((project) => {
    return tasks.description.toLowerCase().includes(search.toLowerCase())
  })


  return (
    <div className="App">
      <Routes>
      <Layout

            tasks={tasks}
            search={search}
            setSearch={setSearch}
            fetchTasks={fetchTasks}>
            <Route
              exact
              path='/'
              render={(routerProps) => <Login {...routerProps} />}
            />
            <Route
              exact
              path='/tasks'
              render={(routerProps) => (
                <Tasks
                  {...routerProps}
                  tas={filterProjects}
                  mode={mode}
                  patchProjects={patchProjects}
                  postProjects={postProjects}
                  handleUpdatingProject={handleUpdatingProject}
                  handleDeleteProject={handleDeleteProject}
                />
              )}
            />
            <Route
              exact
              path='/projects/:id'
              render={(routerProps) => (
                <ProjectDashboard
                  {...routerProps}
                  mode={mode}
                  handleUpdatingProject={handleUpdatingProject}
                  handleDeleteProject={handleDeleteProject}
                />
              )}
            />
          </Layout>
          <Route path='/home' element= {<ToDoList tasks={tasks} loggedIn={loggedIn} deleteTask={deleteTask} handleNewPost={handleNewPost}/>}/> 
          <Route path='/users/new' element = {<Signup addNewUser={addNewUser}/>}/>
          <Route path='/' element = { <Login setLoggedIn={setLoggedIn} />}/>
      </Routes>
    </div>
  );
}

export default App;
