import React, { useState } from "react";
import debounce from "lodash.debounce";

function Search({ searchFunction, loggedIn }) {
  const [search, setSearch] = useState("");
  const [searchResults, setSearchResults] = useState([]);

  const debouncedSearch = debounce(async (searchQuery) => {
    try {
      const response = await fetch(
        `http://localhost:9293/users/${loggedIn.id}/tasks?description=${searchQuery}`
      );
      const data = await response.json();
      setSearchResults(data);
    } catch (error) {
      console.log(error);
    }
  }, 500);

  function handleSearch(e) {
    setSearch(e.target.value);
    debouncedSearch(e.target.value);
  }

  function handleSearchSubmit(e) {
    e.preventDefault();
    debouncedSearch(search);
  }

  return (
    <form className="d-flex" onSubmit={handleSearchSubmit}>
      <input
        className="form-control"
        type="text"
        placeholder="Search tasks"
        onChange={handleSearch}
        id="search"
      />
      <button className="btn btn-outline-info ms-1" id="searchBtn" type="submit">
        SEARCH
      </button>
      <SearchResults results={searchResults} />
    </form>
  );
}

function SearchResults({ results }) {
  return (
    <ul>
      {results.map((result) => (
        <li key={result.id}>{result.description}</li>
      ))}
    </ul>
  );
}

export default Search;
