import React, { useState } from 'react';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';

const CreateTask = ({ modal, toggle, loggedIn, handleNewPost }) => {
const [form, setForm] = useState({
category: '',
todos: '',
dueDate: '',
completed: false,
});

const handleInputChange = (event) => {
const { name, value } = event.target;
setForm({ ...form, [name]: value });
};

const handleSave = (e) => {
e.preventDefault();
fetch('http://localhost:9293/users/${loggedIn.id}/tasks', {
method: 'POST',
headers: {
'Content-type': 'application/json',
},
body: JSON.stringify(form),
})
.then((response) => response.json())
.then((data) => {
handleNewPost();
toggle();
setForm({
category: '',
todos: '',
dueDate: '',
completed: false,
});
});
};

return (
<Modal isOpen={modal} toggle={toggle}>
<ModalHeader toggle={toggle}> Create Task - Due Date: {form.dueDate} | status: {form.completed ? "Yes" : "No"}</ModalHeader>
<ModalBody>
<form onSubmit={handleSave}>
<div className="form-group">
<label>Category</label>
<input
           type="text"
           className="form-control"
           value={form.category}
           onChange={handleInputChange}
           name="category"
           required
         />
</div>
<div className="form-group">
<label>Tasks</label>
<textarea
           rows="5"
           className="form-control"
           value={form.todos}
           onChange={handleInputChange}
           name="todos"
           required
         ></textarea>
</div>
<div className="form-group">
<label>Due-Date</label>
<input
           type="date"
           className="form-control"
           value={form.dueDate}
           onChange={handleInputChange}
           name="dueDate"
           required
         ></input>
</div>
<div className="form-group">
<label>Status</label>
<select
           name="completed"
           className="form-control"
           value={form.completed}
           onChange={handleInputChange}
         >
<option value={false}>Not Completed</option>
<option value={true}>Completed</option>
</select>
</div>
<ModalFooter>
<Button color="primary" type="submit">
Create
</Button>{' '}
<Button color="secondary" onClick={toggle}>
Cancel
</Button>
</ModalFooter>
</form>
</ModalBody>
</Modal>
);
};

export default CreateTask;
