import React from 'react'
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import { useEffect } from 'react';
import { useState } from 'react';

const EditTask = ({ modal, loggedIn, handleNewPost, toggle, taskObj, handleView, handleSave}) => {
    const [form, setForm] = useState({
      category: "",
      todos: "",
      dueDate: ""
    });

    useEffect(() => {
      if (taskObj) {
        setForm({
          category: taskObj.category,
          todos: taskObj.todos,
          dueDate: taskObj.dueDate,
          completed: taskObj.completed,
        });
      } else {
        const today = new Date().toISOString().slice(0, 10);
        setForm({ ...form, dueDate: today });
      }
    }, [taskObj]);
    const handleInputChange = (event) => {
      const { name, value } = event.target;
      setForm({ ...form, [name]: value });
    };

    const handleSubmit = (event) => {
      event.preventDefault();
      handleSave(form);
    };

    const handleUpdate = (e) => {
      fetch(
        `http://localhost:9293/users/${loggedIn.id}/tasks/${taskObj.id}`,
        {
          method: "PATCH",
          headers: {
            "Content-type": "application/json"
          },
          body: JSON.stringify(form)
        }
      );
      handleNewPost();
      toggle();
    };

    return (
      <Modal isOpen={modal} toggle={toggle}>
        <ModalHeader toggle={toggle}> Edit Task </ModalHeader>
        <ModalBody>
          <form>
            <div className="form-group">
              <label>Category</label>
              <input type="text" className="form-control" value={form.category} onChange={handleInputChange} name="category" />
            </div>
            <div className="form-group">
            <label>To-Dos</label>
<input type="text" className="form-control" value={form.todos} onChange={handleInputChange} name="todos" />
</div>
<div className="form-group">
<label>Due Date</label>
<input type="date" className="form-control" value={form.dueDate} onChange={handleInputChange} name="dueDate" />
</div>
<div className="form-check">
<input type="checkbox" className="form-check-input" checked={form.completed} onChange={handleInputChange} name="completed" />
<label className="form-check-label">Completed</label>
</div>
</form>
</ModalBody>
<ModalFooter>
<Button color="primary" onClick={handleSubmit}>Save</Button>
<Button color="secondary" onClick={toggle}>Cancel</Button>
</ModalFooter>
</Modal>
);
};

export default EditTask;
